/*
** ===================================================================
**     Method      :  wirelessSerial_Interrupt (component SynchroMaster)
**
**     Description :
**         The method services the error interrupt of the selected 
**         peripheral(s) and eventually invokes the component's event(s).
**         This method is internal. It is used by Processor Expert only.
** ===================================================================
*/
#define ON_ERROR       0x01U
#define ON_FULL_RX     0x02U
#define ON_RX_CHAR     0x04U
#define ON_FREE_TX     0x08U
#define ON_TX_CHAR     0x10U
#define ON_RX_CHAR_EXT 0x20U
ISR(wirelessSerial_Interrupt)
{
  wirelessSerial_TComData Data = 0U;   /* Temporary variable for data */
  byte Flags = 0U;                     /* Temporary variable for flags */
  byte Status;                         /* Temporary variable for flags */

  Status = SPI1S;                      /* Read the device error register */
  Data = SPI1DL;                       /* Read data from receiver */
  if (SerFlag & CHAR_IN_RX) {          /* Is the overrun error flag set? */
    SerFlag |= OVERRUN_ERR;            /* If yes then set the Overrun error flag */
    Flags |= ON_ERROR;                 /* If yes then set the OnError flag */
  }
  SerFlag |= CHAR_IN_RX;               /* Set flag "char in RX buffer" */
  BufferRead = Data;                   /* Read data from receiver */
  Flags |= ON_RX_CHAR;                 /* Set the OnRxChar flag */
  ErrFlag |= SerFlag;                  /* Update error flag mirror */
  SerFlag &= (byte)(~(byte)FULL_TX);   /* Reset flag "full TX buffer" */

  if (Flags & ON_ERROR) {              /* Is the error flag set? */
    wirelessSerial_OnError();          /* If yes then invoke user event */
  } else {
    if (Flags & ON_RX_CHAR) {          /* Is OnRxChar flag set? */
      wirelessSerial_OnRxChar();       /* If yes then invoke user event */
    }
  }
  wirelessSerial_OnTxChar();           /* If yes then invoke user event */
}

